import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {Router} from "@angular/router";
import {map} from "rxjs";
import {User} from "../model/User";
import {Auftrag} from "../model/Auftrag";

@Injectable({
  providedIn: 'root'
})
export class ApiService {

  apiUrl = 'http://localhost:8080';

  constructor(private http: HttpClient, private router: Router) { }



  createUser(user: User) {
    return this.http.post<any>(this.apiUrl + "/user/create", user).subscribe(data => console.log(data))
  }
  login(username: any, password: any) {
    return this.http.post<any>(this.apiUrl + `/user/login/${username}/${password}`, {username, password});
  }

  addAuftrag(order_id: any, successful: any, price: any, description: any, deadline: any, proof: any, acceptedByHitman: any, proofAcceptedByUser: any): void {
    this.http.post<Auftrag>('/api/auftrag', {order_id,successful,price,description,deadline,proof,acceptedByHitman,proofAcceptedByUser})
      .subscribe(
        (response) => {
          console.log('Auftrag erfolgreich hinzugefügt: ', response);
        },
        (error) => {
          console.log('Fehler beim Hinzufügen des Auftrags: ', error);
        }
      );
  }

}
