import { Component } from '@angular/core';
import {ApiService} from "./service/api.service";
import {User} from "./model/User";
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {Auftrag} from "./model/Auftrag";
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent {
  title = 'frontend';
  form: FormGroup;


  constructor(private api: ApiService, private fb: FormBuilder) {
    this.form = this.fb.group({
      username: ['',Validators.required],
      password: ['',Validators.required]
    });
  }

  createUser(){
    // stop here if form is invalid
    if (this.form.invalid) {
      return;
    }

   const user: User = {
      username: this.form.controls["username"].value,
      password: this.form.controls["password"].value
    }
  this.api.createUser(user);
  }

  login(username: any, password: any){
    // stop here if form is invalid
    if (this.form.invalid) {
      return;
    }

    username = this.form.controls["username"].value;
    password = this.form.controls["password"].value;

    this.api.login(username, password).subscribe(o => {
      console.log(o);
    });
  }



  addAuftrag() {
    // stop here if form is invalid
    if (this.form.invalid) {
      return;
    }

    const auftrag: Auftrag = {
      order_id: this.form.controls["order_id"].value,
      successful: this.form.controls["successful"].value,
      price: this.form.controls["price"].value,
      description: this.form.controls["description"].value,
      deadline: this.form.controls["deadline"].value,
      proof: this.form.controls["proof"].value,
      acceptedByHitman: this.form.controls["acceptedByHitman"].value,
      proofAcceptedByUser: this.form.controls["proofAcceptedByUser"].value
    }
    this.api.addAuftrag(auftrag);
  }

}
  <!-- Hero -->
  <section class="et-hero-tabs">
    <h1>Green-Hitman</h1>
    <h3>Buy your Hitman, with Green-Hitman!</h3>

    <a href=""><img src="https://www.animatedimages.org/data/media/697/animated-vampire-image-0001.gif" border="0" alt="animated-vampire-image-0001" /></a>

    <div class="et-hero-tabs-container">
      <a class="et-hero-tab" href="#tab-es6">Login/Register</a>
      <a class="et-hero-tab" href="#tab-flexbox">Order Hitman</a>
      <a class="et-hero-tab" href="#tab-react">React</a>
      <a class="et-hero-tab" href="#tab-angular">Write Rating</a>
      <a class="et-hero-tab" href="#tab-other">Ratings</a>
      <span class="et-hero-tab-slider"></span>
    </div>
  </section>

  <!-- Main -->
  <main class="et-main">

    <section class="et-slide" id="tab-es6">
      <a href="">
        <h1>Login/Register</h1>
      </a>

      <br>
      <div class="example-container">
        <br>
        <h3>Register</h3>
        <form [formGroup]="form" (ngSubmit)="createUser()">
          <mat-form-field>
            <input matInput placeholder="Username" formControlName="username">
          </mat-form-field>
          <br>
          <mat-form-field>
            <input matInput placeholder="Password" formControlName="password">
          </mat-form-field>
            <button mat-button color="primary" ng>Register</button>
          <!-- other form elements -->
        </form>

        <br>
        <br>
        <h3>Login</h3>
        <form [formGroup]="form" (ngSubmit)="login(username,password)">
          <mat-form-field>
            <input matInput #username placeholder="Username" formControlName="username">
          </mat-form-field>
          <br>
          <mat-form-field>
            <input matInput #password placeholder="Password" formControlName="password">
          </mat-form-field>
          <button mat-button color="primary" ng>Login</button>
          <!-- other form elements -->
        </form>
      </div>
    </section>

    <section class="et-slide" id="tab-flexbox">
      <a href="">
        <h1>Order Hitman</h1>
      </a>

      order_id: number;
      successful: boolean;
      price: number;
      description: string;
      deadline: Date;
      proof: Blob[];
      acceptedByHitman: boolean;
      proofAcceptedByUser: boolean;
      <h3>Login</h3>
      <form [formGroup]="form" (ngSubmit)="addAuftrag()">
        <mat-form-field>
          <input matInput #order_id placeholder="order_id" formControlName="order_id">
        </mat-form-field>
        <br>
        <mat-form-field>
          <input matInput #successful placeholder="successful" formControlName="successful">
        </mat-form-field>
        <br>
        <mat-form-field>
          <input matInput #price placeholder="price" formControlName="price">
        </mat-form-field>
        <br>
        <mat-form-field>
          <input matInput #description placeholder="description" formControlName="description">
        </mat-form-field>
        <br>
        <mat-form-field>
          <input matInput #deadline placeholder="deadline" formControlName="deadline">
        </mat-form-field>
        <br>
        <mat-form-field>
          <input matInput #proof placeholder="proof" formControlName="proof">
        </mat-form-field>
        <br>
        <mat-form-field>
          <input matInput #acceptedByHitman placeholder="acceptedByHitman" formControlName="acceptedByHitman">
        </mat-form-field>
        <br>
        <mat-form-field>
          <input matInput #proofAcceptedByUser placeholder="proofAcceptedByUser" formControlName="proofAcceptedByUser">
        </mat-form-field>
        <br>

        <mat-form-field>
          <input matInput #password placeholder="Password" formControlName="password">
        </mat-form-field>
        <button mat-button color="primary" ng>Login</button>
        <!-- other form elements -->
      </form>

    </section>

    <section class="et-slide" id="tab-react">
      <a href="">
      <h1>React</h1>
      </a>
      <h3>something about react</h3>
    </section>

    <section class="et-slide" id="tab-angular">
      <a href="">
      <h1>Write Rating</h1>
      </a>
      <h3>something about Write Review</h3>
    </section>

    <section class="et-slide" id="tab-other">
      <a href="">
      <h1>Ratings</h1>
      </a>
      <h3>something about Ratings</h3>
    </section>
  </main>
